package com.itheima_01;

public class IfDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("开始");
		
		// 定义两个变量
		int a = 10;
		int b = 20;
		
		if(a == b){
			System.out.println("a等于b");
		}
		
		int c = 10;
		if(a == c){
			System.out.println("a等于c");
		}
		
		System.out.println("结束");
	}

}

